export default {
  name: "windy-plugin-tempaloft",
  setup({ windyAPI }) {
    const { store } = windyAPI;

    const levels = ["FL050", "FL100", "FL180", "FL240", "FL300", "FL340"];
    let idx = 0;
    function update() {
      store.set("overlay", "temp");
      store.set("level", levels[idx]);
    }
    window.tempaloft = {
      nextLevel: () => {
        idx = (idx + 1) % levels.length;
        update();
      }
    };
    update();
  }
};
